pub async fn deploy(
    wallet: &Wallet,
    error: ERC1155ReceiverMock::RevertType,
) -> eyre::Result<Address> {
    let contract =
        ERC20Mock::deploy(wallet, REC_RETVAL, BAT_RETVAL, error)
            .await?;
    Ok(*contract.address())
}
