#![allow(dead_code)]
#![cfg(feature = "e2e")]
use alloy::{
    primitives::{aliases::B32, Address, U256},
    sol,
};
use e2e::Wallet;
use stylus_sdk::{abi::Bytes, function_selector};

sol! {
    #[allow(missing_docs)]
    // Built with Remix IDE; solc v0.8.24+commit.e11b9ed9
    #[sol(rpc, bytecode="")]
    contract ERC721ReceiverMock is IERC721Receiver {
        enum RevertType {
            None,
            RevertWithoutMessage,
            RevertWithMessage,
            RevertWithCustomError,
            Panic
        }

        bytes4 private immutable _retval;
        RevertType private immutable _error;

        #[derive(Debug, PartialEq)]
        event Received(address operator, address from, uint256 tokenId, bytes data);

        error CustomError(bytes4);

        constructor(bytes4 retval, RevertType error) {
            _retval = retval;
            _error = error;
        }

        function onERC721Received(
            address operator,
            address from,
            uint256 tokenId,
            bytes memory data
        ) public returns (bytes4) {
            if (_error == RevertType.RevertWithoutMessage) {
                revert();
            } else if (_error == RevertType.RevertWithMessage) {
                revert("ERC721ReceiverMock: reverting");
            } else if (_error == RevertType.RevertWithCustomError) {
                revert CustomError(_retval);
            } else if (_error == RevertType.Panic) {
                uint256 a = uint256(0) / uint256(0);
                a;
            }

            emit Received(operator, from, tokenId, data);
            return _retval;
        }
    }
}

const RET_VAL: B32 = B32::new(function_selector!(
    "onERC721Received",
    Address,
    Address,
    U256,
    Bytes,
));

pub async fn deploy(
    wallet: &Wallet,
    error: ERC721ReceiverMock::RevertType,
) -> eyre::Result<Address> {
    let contract = ERC721ReceiverMock::deploy(wallet, RET_VAL, error).await?;
    Ok(*contract.address())
}
