#![allow(dead_code)]
#![cfg(feature = "e2e")]
use alloy::{primitives::Address, sol};
use e2e::Wallet;

// contract that replicate USDT approval behavior in approveAndCall
sol! {
    #[allow(missing_docs)]
    // Built with Remix IDE; solc v0.8.21+commit.d9974bed
    #[sol(rpc, bytecode="")]
    // SPDX-License-Identifier: MIT
    contract ERC1363ForceApproveMock is ERC1363 {
        constructor() ERC20("ERC1363ForceApproveMock", "MCK") {}

        function approveAndCall(
            address spender,
            uint256 amount,
            bytes memory data
        ) public virtual override returns (bool) {
            require(
                amount == 0 || allowance(msg.sender, spender) == 0,
                "USDT approval failure"
            );
            return super.approveAndCall(spender, amount, data);
        }

        function forceApprove(
            address owner,
            address spender,
            uint256 amount
        ) public {
            super._approve(owner, spender, amount);
        }

        function allowance(address owner, address spender)
            public
            view
            override(ERC20, IERC20)
            returns (uint256)
        {
            return super.allowance(owner, spender);
        }
    }
}

/// Deploy an ERC1363 mock contract.
pub async fn deploy(wallet: &Wallet) -> eyre::Result<Address> {
    let contract = ERC1363ForceApproveMock::deploy(wallet).await?;
    Ok(*contract.address())
}
